import React from 'react';
import './index.css';
import Blog from './components/Blog';
import {useState, useEffect} from 'react';
import blogservice from './services/blogservice'
import loginService from './services/login'

const App =()=> {

const [username, addusername]=useState('')
const [password, addpassword]=useState('')
const [errorMessage, setErrorMessage] = useState(null)
const [user, setuser]=useState(null)

const handleLogin = async (event) => {
    event.preventDefault()
    try {
      const user = await loginService.login({
        username, password,
      })
      blogservice.setToken(user.token)
      setuser(user)
      addusername('')
      addpassword('')
    } catch (exception) {
      setErrorMessage('Tarkista kirjautumistiedot')
      setTimeout(() => {
        setErrorMessage(null)
      }, 5000)
    }
  } 

const [blogs, addblogs] =useState([])
  useEffect(() => {
   blogservice
    .getAll()
     .then(blogi=>
      addblogs(blogi))
    }, [])

  const loginForm =()=> (
      <div>
        <h2>Log in to application</h2>
        <form onSubmit={handleLogin}>
          Name:       <input value={username} onChange={({ target }) => addusername(target.value)}/>
          <br/>
          Password:   <input type="password" value={password} onChange={({ target }) => addpassword(target.value)}/>
          <br/>
          <button type="submit">Sign in</button>
        </form>
      </div>
  )

  const bloglist=()=>(
      <div>
        <h2>Blogs</h2>
        {blogs.map(blog =>
          <Blog key={blog.id} blog={blog} />
        )}
      </div>
    )

    return (
      <><h1>Blogit</h1>
      {user === null ?
        loginForm() :
        <div>
          <p>{user.name} logged in</p>
          {bloglist()}
        </div>
      }</>
    ) 
}

export default App;
