import React from 'react';
import './index.css';
import Blog from './components/Blog';
import {useState, useEffect} from 'react';
import blogservice from './services/blogservice'
import loginService from './services/login'

const App =()=> {

const [username, addusername]=useState('')
const [password, addpassword]=useState('')
const [errorMessage, setErrorMessage] = useState(null)
const [user, setuser]=useState(null)
const [title, addtitle]=useState('')
const [author, addauthor]=useState('')
const [url, addurl]=useState('')

const add =async(event)=> {
  event.preventDefault()
  if (title===''|| url===''){
    setErrorMessage('T채yt채 kaikki kent채t')
  }else {
    try {
      const blogi={
        title:event.target.title,
        author:event.target.author,
        url:event.target.url
      }
      await blogservice.create(blogi)
      addtitle('')
      addauthor('')
      addurl('')
    }
    catch(exception){
      setErrorMessage('Blogin lis채ys ei onnistunut')
    }
  }
}

const handleLogin = async (event) => {
    event.preventDefault()
    try {
      const user = await loginService.login({
        username, password,
      })
      blogservice.setToken(user.token)
      setuser(user)
      addusername('')
      addpassword('')
    } catch (exception) {
      setErrorMessage('Tarkista kirjautumistiedot')
      setTimeout(() => {
        setErrorMessage(null)
      }, 5000)
    }
  } 
const ulos =async (event) =>{
  blogservice.setToken(null)
  setuser(null)
}

const [blogs, addblogs] =useState([])
  useEffect(() => {
   blogservice
    .getAll()
     .then(blogi=>
      addblogs(blogi))
    }, [])

  const loginForm =()=> (
      <div>
        <h2>Log in to application</h2>
        <form onSubmit={handleLogin}>
          Name:       <input value={username} onChange={({ target }) => addusername(target.value)}/>
          <br/>
          Password:   <input type="password" value={password} onChange={({ target }) => addpassword(target.value)}/>
          <br/>
          <button type="submit">Sign in</button>
        </form>
      </div>
  )

  const bloglist=()=>(
      <div>
        <h2>Blogs</h2>
        {blogs.map(blog =>
          <Blog key={blog.id} blog={blog} />
        )}
      </div>
    )

    return (
      <><h1>Blogit</h1>
      {user === null ?
        loginForm() :
        <div>
          <p>{user.name} logged in</p>
          {bloglist()}
          <form onSubmit={add}>
            title:  <input value={title} onChange={({ target }) => addtitle(target.value)} /><br/>
            author: <input value={author} onChange={({ target }) => addauthor(target.value)} /><br/>
            url:    <input value={url} onChange={({ target }) => addurl(target.value)}onChange={addurl} /><br/>
                <button type="submit" >lis채채</button>
                </form>
          <button onChange={ulos}>Kirjaudu ulos</button>
        </div>
      }</>
    ) 
}

export default App;
